
public class Examples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		String b="a";
		int c=a+b; //String to int
		
		System.out.println("c is "+c);
		
		int x=1;
		boolean y=true;
		int z=(x+y); //Boolean to int
		
		System.out.println("z is "+z);
	}

}
